import { vop } from "./vop.js";
const every = vop();
every.default((v) => {
  for (let i = v.length; i-- > 0; ) {
    if (!v[i]) return false;
  }
  return true;
});
const every2 = every.add(2, (a) => a[0] && a[1]);
const every3 = every.add(3, (a) => a[0] && a[1] && a[2]);
const every4 = every.add(4, (a) => a[0] && a[1] && a[2] && a[3]);
export {
  every,
  every2,
  every3,
  every4
};
